/*1. Napisati program koji prvo pročita koliko redaka ima datoteka, tj. koliko ima studenata
zapisanih u datoteci. Nakon toga potrebno je dinamički alocirati prostor za niz struktura
studenata (ime, prezime, bodovi) i učitati iz datoteke sve zapise. Na ekran ispisati ime,
prezime, apsolutni i relativni broj bodova.
Napomena: Svaki redak datoteke sadrži ime i prezime studenta, te broj bodova na kolokviju.
relatvan_br_bodova = br_bodova/max_br_bodova*100*/#define _CRT_SECURE_NO_WARNINGS#include <stdio.h>#include <stdlib.h>#define MAX (100)typedef struct student{	char ime[MAX];	char prezime[MAX];	double bodovi;} stud;int citajRedak(){	int brojac = 0;	FILE* f = NULL;	char buffer[MAX] = { 0 };	f = fopen("txt.txt", "r");	if (!f)	{		printf("file not open");		return -1;	}	while (!feof(f))	{		fgets(buffer, sizeof(buffer),f);		brojac++;	}	fclose(f);	return brojac;}stud* alokacija(int n){	stud* s = NULL;	s = (stud*)malloc(n * sizeof(stud));	return s;}int upis(int n, stud* s){	FILE* f = NULL;	f = fopen("txt.txt", "r");	if (f == NULL)	{		printf("greska");		return -1;	}	for (int i = 0; i < n; i++)	{		fscanf(f, "%s %s %d", s[i].ime, s[i].prezime, &s[i].bodovi);	}	fclose(f);	return 1;}int ispis(int n, stud* s){	printf("\n ime:\t prezime\t RB\tAB\t");	for (int i = 0; i < n; i++) {		printf("%s\t%s\t%d\t%lf\n", (s + 1)->ime, (s + 1)->prezime, (s + 1)->bodovi,(double) (s + 1)->bodovi / 50);	}	return 1;}int main(){	int n;	stud* s = NULL;	n = citajRedak();	printf("broj redaka je %d", n);	s = alokacija(n);	upis(n, s);	ispis(n, s);	return 0;}